from builtins import object


class PrePostCmdManager(object):
    """
    A class to manager the precmd and postcmd commands such as used in begin, end, abort, resume, pause.
    """
    _begin_precmd = None
    _begin_postcmd = None
    _abort_precmd = None
    _abort_postcmd = None
    _end_precmd = None
    _end_postcmd = None
    _pause_precmd = None
    _pause_postcmd = None
    _resume_precmd = None
    _resume_postcmd = None

    @property
    def begin_precmd(self):
        """
        The function to call before the begin command
        """
        return self._begin_precmd

    @begin_precmd.setter
    def begin_precmd(self, begin_precmd):
        """
        Set the function to call before the begin command
        """
        self._begin_precmd = begin_precmd

    @property
    def begin_postcmd(self):
        """
        The function to call after the begin command
        """
        return self.begin_postcmd

    @begin_postcmd.setter
    def begin_postcmd(self, begin_postcmd):
        """
        Set the function to call after the begin command
        """
        self._begin_postcmd = begin_postcmd

    @property
    def abort_precmd(self):
        """
        The function to call before the abort command
        """
        return self._abort_precmd

    @abort_precmd.setter
    def abort_precmd(self, abort_precmd):
        """
        Set the function to call before the abort command
        """
        self._abort_precmd = abort_precmd

    @property
    def abort_postcmd(self):
        """
        The function to call after the abort command
        """
        return self.abort_postcmd

    @abort_postcmd.setter
    def abort_postcmd(self, abort_postcmd):
        """
        Set the function to call after the abort command
        """
        self._abort_postcmd = abort_postcmd

    @property
    def end_precmd(self):
        """
        The function to call before the end command
        """
        return self._end_precmd

    @end_precmd.setter
    def end_precmd(self, end_precmd):
        """
        Set the function to call before the end command
        """
        self._end_precmd = end_precmd

    @property
    def end_postcmd(self):
        """
        The function to call after the end command
        """
        return self.end_postcmd

    @end_postcmd.setter
    def end_postcmd(self, end_postcmd):
        """
        Set the function to call after the end command
        """
        self._end_postcmd = end_postcmd

    @property
    def pause_precmd(self):
        """
        The function to call before the pause command
        """
        return self._pause_precmd

    @pause_precmd.setter
    def pause_precmd(self, pause_precmd):
        """
        Set the function to call before the pause command
        """
        self._pause_precmd = pause_precmd

    @property
    def pause_postcmd(self):
        """
        The function to call after the pause command
        """
        return self.pause_postcmd

    @pause_postcmd.setter
    def pause_postcmd(self, pause_postcmd):
        """
        Set the function to call after the pause command
        """
        self._pause_postcmd = pause_postcmd

    @property
    def resume_precmd(self):
        """
        The function to call before the resume command
        """
        return self._resume_precmd

    @resume_precmd.setter
    def resume_precmd(self, resume_precmd):
        """
        Set the function to call before the resume command
        """
        self._resume_precmd = resume_precmd

    @property
    def resume_postcmd(self):
        """
        The function to call after the resume command
        """
        return self.resume_postcmd

    @resume_postcmd.setter
    def resume_postcmd(self, resume_postcmd):
        """
        Set the function to call after the resume command
        """
        self._resume_postcmd = resume_postcmd
