from typing import Protocol, runtime_checkable, Tuple
from collections.abc import Callable


@runtime_checkable
class GeniePvConnectionProtocol(Protocol):

    @staticmethod
    def set_pv_value(name: str, value: any, wait: bool, timeout: int,
                     safe_not_quick: bool) -> None:
        pass

    @staticmethod
    def clear_monitor(name: str, timeout: int) -> None:
        pass

    @staticmethod
    def get_pv_value(name: str, to_string: bool, timeout: int,
                     use_numpy: bool | None) -> any:
        pass

    @staticmethod
    def get_pv_timestamp(name: str, timeout: int) -> Tuple[int, int]:
        pass

    @staticmethod
    def pv_exists(name: str, timeout: int) -> bool:
        pass

    @staticmethod
    def add_monitor(name: str, call_back_function: Callable[[any, any, any], None],
                    link_alarm_on_disconnect: bool,
                    to_string: bool, use_numpy: bool | None) -> Callable[[], None]:
        pass






