    def test_GIVEN_g_non_existing_command_WHEN_call_THEN_error_message(self):
        script_lines = ["from genie_python import genie as g\n"
                        "def test():\n"
                        "  g.aitfor_time(10)"]
        temp_script_file = write_to_file(script_lines)
        result = self.checker.check_script(temp_script_file.name, self.machine)

        os.unlink(temp_script_file.name)
        self.assertEquals(result, ["E:  3: Module 'genie_python.genie' has no "
                                   "'aitfor_time' member; maybe 'waitfor_time'? (no-member)"])

    def test_GIVEN_python_file_WHEN_call_THEN_give_lists_of_attributes(self):
        script_lines = ["def test():\n",
                        "    b = 10\n",
                        "class ScriptChecker(Object):\n",
                        "    def __init__(self):\n",
                        "        self.test = 10\n",
                        "GLOBAL = 10"]
        result = ""
        dir_path = tempfile.mkdtemp()
        temp_file = tempfile.NamedTemporaryFile(suffix = ".py", delete=False, dir=dir_path)
        for line in script_lines:
            temp_file.write(line)

        temp_file.close()
        result = self.checker.get_inst_attributes(dir_path)
        print("RESULT  " + result)
        shutil.rmtree(dir_path)
        self.assertEquals(result, "inst.test,inst.ScriptChecker,inst.ScriptChecker.test,inst.GLOBAL")
